import{_ as e,c as o,o as t,d as a}from"./app.0cb80d29.js";const q='{"title":"How to learning or why to learning","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4F60\u5FC5\u987B\u61C2\u5F97\u5B66\u4E60\u7A7A\u95F4\u96C6","slug":"\u4F60\u5FC5\u987B\u61C2\u5F97\u5B66\u4E60\u7A7A\u95F4\u96C6"},{"level":2,"title":"\u6BCF\u4E2A\u4EBA\u90FD\u8981\u77E5\u9053\u7684\u79D8\u6CD5","slug":"\u6BCF\u4E2A\u4EBA\u90FD\u8981\u77E5\u9053\u7684\u79D8\u6CD5"}],"relativePath":"juvenile/How-to-learning/index.md","lastUpdated":1642996261784}',r={},n=a('<h1 id="how-to-learning-or-why-to-learning" tabindex="-1">How to learning or why to learning <a class="header-anchor" href="#how-to-learning-or-why-to-learning" aria-hidden="true">#</a></h1><hr><h2 id="\u4F60\u5FC5\u987B\u61C2\u5F97\u5B66\u4E60\u7A7A\u95F4\u96C6" tabindex="-1">\u4F60\u5FC5\u987B\u61C2\u5F97\u5B66\u4E60\u7A7A\u95F4\u96C6 <a class="header-anchor" href="#\u4F60\u5FC5\u987B\u61C2\u5F97\u5B66\u4E60\u7A7A\u95F4\u96C6" aria-hidden="true">#</a></h2><blockquote><p>\u5B66\u4E60\u65F6\u95F4\u975E\u5E38\u75DB\u82E6\u7684\u4E8B\u60C5,\u5728\u4F60\u5DE5\u4F5C\u7684\u65F6\u5019\u5B66\u4E60\u662FVery difficult,\u6240\u4EE5\u4F60\u5FC5\u987B\u5728\u4F60\u4F11\u606F\u7684\u65F6\u5019\u53BB\u96C6\u4E2D\u5168\u529B\u7684\u53BB&quot;\u523B\u82E6&quot;\u5B66\u4E60,\u65E0\u8BBA\u4F60\u4EE5\u524D\u662F\u4EC0\u4E48\u6837\u5B50,\u4EBA\u4E0D\u80FD\u603B\u662F\u5F80\u524D\u770B\u5C3D\u7BA1\u4F60\u4EE5\u524D\u662F\u4E0D\u5B66\u65E0\u672F\u7684\u4EBA\u8FD8\u662F\u540A\u513F\u90CE\u5F53\u7684\u4EBA,\u4F60\u5728\u8FD9\u4E00\u523B\u4F60\u5C31\u5E94\u8BE5\u660E\u767D&quot;\u751F\u6D3B&quot;\u8FD9\u4E24\u4E2A\u5B57\u4E0D\u662F\u770B\u7740\u7B80\u5355\u800C\u5DF2,\u4F60\u5FC5\u987B\u6E05\u695A\u4F60\u5FC5\u987B\u8981\u505A\u5230\u751F,\u624D\u80FD\u6D3B\u4E0B\u53BB.\u4E16\u754C\u4E0A\u4EBA\u90A3\u4E48\u591A\u4F60\u5982\u4F55\u8131\u9896\u800C\u51FA\u505A\u4E00\u4E2A\u4E0D\u540C\u4E8E\u5176\u4ED6\u4EBA\u7684\u7480\u74A8&quot;\u660E\u661F&quot;.\u4F60\u79BB\u5F00\u8FD9\u4E2A\u4E16\u754C\u7684\u65F6\u5019\u4F60\u662F\u5E26\u4E0D\u8D70\u4EC0\u4E48\u7684,\u4F60\u80FD\u7559\u4E0B\u6765\u7684\u4E5F\u4E0D\u4F1A\u4F20\u627F\u5343\u5E74.\u4F60\u6240\u8C13\u7684\u77E5\u8BC6\u53EA\u4E0D\u8FC7\u662F\u524D\u4EBA\u7528\u6BD5\u751F\u6240\u77E5\u8BB0\u5F55\u4E0B\u4E86\u4F60\u66FE\u6CA1\u6709\u8D70\u8FC7\u7684\u65F6\u95F4\u7247\u6BB5.\u6240\u4EE5\u4F60\u80FD\u591F\u638C\u63E1\u4F4F\u4F60\u7684\u65F6\u95F4\u4F60\u5C31\u8D85\u8D8A\u4E86\u5927\u90E8\u5206\u4EBA.\u7136\u540E\u4F60\u89C2\u6469\u4ED6\u4EBA\u7684\u6BD5\u751F\u6240\u5B66\u6765\u6F14\u7ECE\u597D\u81EA\u5DF1\u4E00\u751F\u7684\u77E5\u8BC6\u5B66\u5E93,\u8BA9\u4F60\u7684\u77E5\u8BC6\u4E5F\u80FD\u7ED9\u4ED6\u4EBA\u5E26\u6765\u5E2E\u52A9.\u5E0C\u671B\u4F60\u770B\u5230\u8FD9\u91CC\u4F60\u5C31\u5E94\u8BE5\u660E\u767D!\u65F6\u95F4\u662F\u4F60\u7684life.\u77E5\u8BC6\u80FD\u591F\u8BA9\u4F60\u7684\u7075\u9B42\u5347\u534E,\u8FD9\u4E2A\u4E16\u754C\u5F88\u5927\u4F60\u4E0D\u53EF\u80FD\u6BCF\u4E2A\u77E5\u8BC6\u90FD\u80FD\u5B66\u5230,\u601D\u4F60\u6240\u60F3,\u60F3\u4F60\u6240\u53D7,\u7ED3\u5408\u4F60\u7684\u5DE5\u4F5C\u4F60\u7684\u751F\u6D3B\u7B49\u7B49\u53BB\u5B66\u4E60\u4F60\u611F\u5174\u8DA3\u7684\u4E1C\u897F,\u6240\u8C13\u7684\u5174\u8DA3\u662F\u80FD\u591F\u8BA9\u4F60\u77ED\u65F6\u95F4\u5B66\u4F1A\u4F60\u60F3\u5B66\u7684\u77E5\u8BC6\u5E76\u4E14\u4F60\u80FD\u53D7\u5176\u6240\u7528.\u65E0\u8BBA\u4F60\u5230\u6700\u540E\u4F60\u83B7\u5F97\u4E86\u4EC0\u4E48,\u4F60\u90FD\u5E94\u8BE5\u5F00\u5FC3,\u4F60\u770B\u5230\u4E86\u4E0D\u4E00\u6837\u7684\u81EA\u5DF1\u5E76\u4E14\u770B\u5230\u4E86\u81EA\u5DF1\u7AD9\u5728\u5DC5\u5CF0\u7684\u6837\u5B50.\u90A3\u662F\u7684\u4F60\u505A\u4EC0\u4E48\u90FD\u4F1A\u5F88\u987A\u5FC3,\u505A\u4EC0\u4E48\u90FD\u6709\u65E0\u540D\u7684\u81EA\u4FE1.\u5E76\u4E14\u8BA9\u4F60\u4E00\u76F4\u505A\u4E0B\u53BB.\u65E0\u8BBA\u4F60\u5728\u54EA\u4E2A\u884C\u4E1A\u4F60\u90FD\u5E94\u8BE5\u653E\u4E0B\u4F60\u624B\u4E2D\u7684\u7816\u5934(\u624B\u673A),\u4ED6\u7ED9\u4F60\u5E26\u6765\u7684\u4FE1\u606F\u5F88\u6709\u9650,\u4F60\u4E0D\u77E5\u9053\u4ED6\u8DDF\u4F60\u8BF4\u7684\u662F\u771F\u7684\u662F\u5047\u7684.\u4E8B\u60C5\u773C\u89C1\u624D\u662F\u771F.\u51E1\u4E8B\u7528\u5FC3\u542C\u624D\u7B97\u771F.\u4F60\u4E0D\u5E94\u8BE5\u76F8\u4FE1\u5916\u754C\u7ED9\u4F60\u7684\u4E00\u5207\u4FE1\u606F.\u7528\u5FC3\u611F\u53D7\u5230\u7684\u624D\u662F\u771F\u7684.\u613F\u4F60\u5728\u4EE5\u540E\u7684\u65E5\u5B50\u80FD\u591F\u4E58\u98CE\u7834\u6D6A,\u5B66\u4E60\u4E0D\u6B62\u4E8E\u6280\u672F.</p></blockquote><h2 id="\u6BCF\u4E2A\u4EBA\u90FD\u8981\u77E5\u9053\u7684\u79D8\u6CD5" tabindex="-1">\u6BCF\u4E2A\u4EBA\u90FD\u8981\u77E5\u9053\u7684\u79D8\u6CD5 <a class="header-anchor" href="#\u6BCF\u4E2A\u4EBA\u90FD\u8981\u77E5\u9053\u7684\u79D8\u6CD5" aria-hidden="true">#</a></h2><blockquote><p>\u6A21\u4EFF\u662F\u751F\u7269\u6709\u4E86\u7C92\u5B50\u5C31\u4F1A\u6709\u7684\u8C8C\u4F3C\u7CBE\u795E\u7684\u884C\u4E3A.\u5C24\u5176\u5728\u56FD\u5185\u7684\u4EBA,\u4F60\u80FD\u770B\u5230\u65E0\u8BBA\u662F\u5927\u4E8B\u8FD8\u662F\u5C0F\u4E8B\u53EA\u8981\u6709\u4E00\u4E2A\u4EBA\u505A\u51FA\u76F8\u5F53\u7684\u6837\u5B50,\u5C31\u4F1A\u6709\u65E0\u6570\u4E2A\u4EBA\u524D\u8D74\u540E\u7EE7\u7684\u6A21\u4EFF,\u6709\u4E9B\u4EBA\u9760\u7740\u81EA\u5DF1\u6240\u8C13\u7684&quot;\u806A\u660E\u4F36\u4FD0&quot;\u8D5A\u4E86\u4E00\u6CE2\u7EA2\u5229.\u6709\u4E00\u6CE2\u4EBA\u53EA\u662F\u6A21\u4EFF\u5230\u4E86\u76AE\u6BDB,\u5230\u6700\u540E\u4E00\u76F4\u5728\u6A21\u4EFF\u7684\u8DEF\u4E0A\u53EA\u80FD\u6A21\u4EFF\u522B\u4EBA\u7684\u751F\u6D3B\u82DF\u5EF6\u6B8B\u5598\u7684\u751F\u6D3B\u4E00\u8F88\u5B50.\u6A21\u4EFF\u8FD9\u4EF6\u4E8B\u60C5\u662F\u597D\u4E8B\u4F46\u662F\u5982\u679C\u4F60\u505A\u4E0D\u5230\u524D\u8005\u4F60\u5C31\u53EA\u80FD\u5728\u5E95\u5C42\u8DEF\u4E0A\u8D8A\u8D70\u8D8A\u8FDC.\u4F60\u7684\u601D\u7EF4\u6709\u591A\u5E7F\u4F60\u770B\u5F85\u7684\u4E8B\u60C5\u8DDF\u5B83\u53D1\u5C55\u7684\u6837\u5B50\u4F60\u5C31\u80FD\u5C3D\u6536\u773C\u5E95.\u65E0\u8BBA\u4E2D\u95F4\u4F60\u9047\u5230\u4E86\u591A\u5C11\u56F0\u96BE\u4F60\u90FD\u80FD\u591F\u53BB\u6E38\u5203\u6709\u4F59\u7684\u89E3\u51B3.\u56E0\u4E3A\u5B83\u4EEC\u53EA\u662F\u8BA9\u4F60\u7684\u884C\u4E3A\u4E30\u5BCC\u591A\u5F69\u800C\u5DF2.\u62E5\u6709\u77E5\u8BC6\u6D77\u6D0B\u7684\u4F60\u65E0\u8BBA\u5230\u54EA\u91CC\u90FD\u6709\u4F60\u7684\u53D1\u6325\u70B9.</p></blockquote><blockquote><p>\u77E5\u8BC6\u5B66\u6765\u4E4B\u540E\u8FD8\u662F\u53BB\u7528\u7684,\u81F3\u4E8E\u600E\u4E48\u7528,\u5982\u4F55\u53BB\u7528,\u7528\u5230\u4EC0\u4E48\u6837\u7684\u5730\u6B65\u662F\u6211\u4EEC\u53BB\u5B9E\u8DF5\u7684\u65F6\u5019\u4E00\u70B9\u70B9\u79EF\u7D2F\u7684\u7ECF\u9A8C.\u8FD9\u79CD\u7ECF\u9A8C\u662F\u5B66\u4E0D\u6765\u7684,\u65E0\u8BBA\u4F60\u5982\u4F55\u53BB\u6A21\u4EFF,\u4F60\u5728\u4E0D\u540C\u65F6\u7A7A\u78B0\u5230\u7684\u65F6\u95F4\u8DDF\u7C92\u5B50\u662F\u622A\u7136\u4E0D\u540C\u7684\u6240\u4EE5\u4F60\u5B9E\u8DF5\u6765\u7684\u7ADF\u7136\u8DDF\u5176\u4ED6\u4EBA\u5206\u4EAB\u7684\u65F6\u5019\u4ED6\u53EA\u80FD\u5728\u76F8\u540C\u7684\u65F6\u7A7A\u624D\u80FD\u505A\u5230\u4E00\u6A21\u4E00\u6837\u7684\u6548\u679C,\u8FD9\u6837\u59CB\u7EC8\u8BA9\u4EBA\u4E0D\u80FD\u63A5\u53D7\u8FD9\u4E2A\u7ED3\u679C.\u6240\u4EE5\u4F60\u6709\u4E86\u60F3\u6CD5\u4F60\u5C31\u8981\u5927\u80C6\u7684\u53BB\u5C1D\u8BD5,\u65E0\u8BBA\u7ED3\u679C\u662F\u5982\u4F55\u5230\u6700\u540E\u4F60\u7684\u65F6\u7A7A\u7ED9\u4F60\u7684\u7B54\u6848\u59CB\u7EC8\u4E0D\u6BD4\u5176\u4ED6\u4EBA\u83B7\u5F97\u7684\u5C11.\u5728\u4E2D\u95F4\u4E0D\u8981\u8BA9\u4F60\u7684\u5E73\u884C\u65F6\u7A7A\u5E72\u6270\u4F60.\u4F60\u80FD\u63A7\u5236\u7684\u5C31\u662F\u81EA\u5DF1\u7684\u65F6\u7A7A.\u4E0D\u8981\u628A\u5176\u4ED6\u65F6\u7A7A\u7684\u4E1C\u897F\u63BA\u6742\u8FDB\u6765.\u4F60\u53EA\u8FC7\u4E0D\u5B87\u5B99\u4E2D\u770B\u4E0D\u89C1\u7684\u7C92\u5B50\u7F62\u4E86.\u4F60\u5C3D\u53EF\u80FD\u7684\u638C\u63E1\u66F4\u591A\u7684\u7C92\u5B50\u8BA9\u4F60\u53D8\u5F97\u5F3A\u5927\u8D77\u6765.</p></blockquote><blockquote><p>\u5927\u9053\u7406\u6BCF\u4E2A\u4EBA\u90FD\u61C2,\u4E3A\u4EC0\u4E48\u4E00\u4E9B\u4EBA\u542C\u4E86\u5C31\u89C9\u5F97\u8FD9\u4E9B\u9053\u7406\u6211\u90FD\u542C\u4E86\u516B\u5341\u904D\u4E86,\u6211\u90FD\u542C\u70E6\u4E86,\u4F60\u9664\u4E86\u5927\u9053\u7406\u4F60\u8BB2\u4E0D\u51FA\u4EC0\u4E48\u7684,\u4E3A\u4EC0\u4E48\u8FD9\u4E9B\u4EBA\u4F1A\u8FD9\u4E48\u8BA4\u4E3A,\u56E0\u4E3A\u6211\u6CA1\u529E\u6CD5\u5728\u4ED6\u7684\u65F6\u7A7A\u53BB\u5E72\u6270\u4ED6\u7684\u4E8B\u60C5,\u6211\u4E0D\u80FD\u5229\u7528\u5E73\u884C\u65F6\u7A7A\u7ED9\u4ED6\u5C55\u793A\u8FD9\u6837\u4ED6\u53EA\u4F1A\u6A21\u4EFF\u5230\u4E86\u6211\u7684\u505A\u6CD5,\u6CA1\u6709\u611F\u53D7\u5230\u4E2D\u95F4\u9053\u7406.\u800C\u6709\u4E00\u4E9B\u4EBA\u542C\u4E86\u9053\u7406\u4E4B\u540E,\u603B\u662F\u80FD\u591F\u5E73\u9759\u4E0B\u6765\u5728\u4ED4\u7EC6\u7684\u60F3\u60F3\u6211\u4E4B\u524D\u505A\u8FC7\u7684\u4E8B\u60C5\u6709\u6CA1\u6709\u66F4\u597D\u8FDB\u6B65\u7684\u5730\u65B9,\u5927\u9053\u7406\u53EA\u662F\u7ED9\u4E86\u6211\u4E00\u4E2A\u65B9\u5411\u6211\u80FD\u591F\u628A\u6211\u6240\u5B66\u6240\u60F3\u8FD0\u7528\u7684\u771F\u6B63\u7684\u5B9E\u8DF5\u4E0A\u53BB,\u8FD9\u624D\u662F\u90A3\u4E9B\u5927\u80FD\u4E4B\u4EBA.\u4ED6\u4EEC\u8BFB\u4E00\u4E2A\u9053\u7406\u4E0D\u662F\u770B\u6211\u770B\u6CA1\u770B\u5230\u8FC7,\u800C\u662F\u6DF1\u5C42\u6B21\u7684\u8003\u8651\u5176\u4E2D\u7684\u5965\u79D8,\u56E0\u4E3A\u8FD9\u4E9B\u9053\u7406\u662F\u5728\u4E0D\u540C\u7684\u65F6\u7A7A\u4EA7\u751F\u7684,\u4F60\u5728\u53E6\u4E00\u4E2A\u65F6\u7A7A\u4E0D\u80FD\u5B8C\u7F8E\u7684\u9610\u8FF0\u4ED6\u7684\u5C42\u6B21,\u53EA\u80FD\u501F\u9274\u5176\u4E2D\u539F\u7406\u4E00\u70B9\u4E00\u70B9\u5B9E\u8DF5\u7136\u540E\u518D\u6765\u770B,\u518D\u53BB\u89E3\u6790\u4E2D\u95F4\u66F4\u6DF1\u5C42\u6B21\u7684\u5965\u79D8.\u8FD9\u624D\u662F\u6211\u4EEC\u6BCF\u4E2A\u7C92\u5B50\u53BB\u656C\u4F69\u7684\u6837\u5B50.\u5E0C\u671B\u5B66\u4E60\u80FD\u5E26\u7ED9\u4F60\u4F60\u6240\u60F3\u7684,\u4E5F\u80FD\u5E26\u7ED9\u4F60\u4F60\u6240\u60F3\u4E0D\u5230\u7684.</p></blockquote><hr><blockquote><p>\u672A\u5B8C\u5F85\u7EED...</p></blockquote>',10),l=[n];function i(c,d,h,u,s,_){return t(),o("div",null,l)}var b=e(r,[["render",i]]);export{q as __pageData,b as default};
