import{_ as e,c as a,o as t,d as r}from"./app.0cb80d29.js";const _='{"title":"\u901A\u8FC7\u8D39\u66FC\u5B66\u4E60\u6CD5\u66F4\u5FEB\u5730\u5B66\u4E60\u4E8B\u7269","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4EC0\u4E48\u662F\u8D39\u66FC\u5B66\u4E60\u6CD5?","slug":"\u4EC0\u4E48\u662F\u8D39\u66FC\u5B66\u4E60\u6CD5"},{"level":3,"title":"\u7B2C\u4E00\u6B65","slug":"\u7B2C\u4E00\u6B65"},{"level":3,"title":"\u7B2C\u4E8C\u6B65","slug":"\u7B2C\u4E8C\u6B65"},{"level":3,"title":"\u7B2C\u4E09\u6B65","slug":"\u7B2C\u4E09\u6B65"},{"level":3,"title":"\u7B2C\u56DB\u6B65","slug":"\u7B2C\u56DB\u6B65"}],"relativePath":"sansx/Feynman_Technique/index.md","lastUpdated":1642996261784}',n={},i=r('<h1 id="\u901A\u8FC7\u8D39\u66FC\u5B66\u4E60\u6CD5\u66F4\u5FEB\u5730\u5B66\u4E60\u4E8B\u7269" tabindex="-1">\u901A\u8FC7\u8D39\u66FC\u5B66\u4E60\u6CD5\u66F4\u5FEB\u5730\u5B66\u4E60\u4E8B\u7269 <a class="header-anchor" href="#\u901A\u8FC7\u8D39\u66FC\u5B66\u4E60\u6CD5\u66F4\u5FEB\u5730\u5B66\u4E60\u4E8B\u7269" aria-hidden="true">#</a></h1><blockquote><p>\u8D44\u6599\u5730\u5740: <a href="https://www.cantorsparadise.com/learn-anything-faster-by-using-the-feynman-technique-6565a9f7eda7" target="_blank" rel="noopener noreferrer">Learn Anything Faster By Using The Feynman Technique</a></p></blockquote><h2 id="\u4EC0\u4E48\u662F\u8D39\u66FC\u5B66\u4E60\u6CD5" tabindex="-1">\u4EC0\u4E48\u662F\u8D39\u66FC\u5B66\u4E60\u6CD5? <a class="header-anchor" href="#\u4EC0\u4E48\u662F\u8D39\u66FC\u5B66\u4E60\u6CD5" aria-hidden="true">#</a></h2><p>\u8D39\u66FC\u5B66\u4E60\u6CD5\u4E3B\u8981\u5305\u542B\u4EE5\u4E0B\u56DB\u6B65:</p><ol><li>\u9009\u62E9\u4E00\u4E2A\u4F60\u60F3\u4E86\u89E3\u5B66\u4E60\u7684\u4E3B\u9898</li><li>\u5047\u88C5\u4F60\u6B63\u5728\u8BFE\u5802\u5411\u4ED6\u4EBA\u6559\u6388\u8FD9\u4E2A\u4E3B\u9898\u5185\u5BB9</li><li>\u5F53\u4F60\u5728\u67D0\u5904\u5361\u58F3\u65F6\uFF0C\u5BFB\u627E\u8D44\u6599\u6765\u89E3\u51B3</li><li>\u4E0D\u65AD\u7B80\u5316\u5E76\u91CD\u7EC4\u77E5\u8BC6\u7ED3\u6784</li></ol><h3 id="\u7B2C\u4E00\u6B65" tabindex="-1">\u7B2C\u4E00\u6B65 <a class="header-anchor" href="#\u7B2C\u4E00\u6B65" aria-hidden="true">#</a></h3><p>\u5C3D\u7BA1\u5B83\u88AB\u79F0\u4E3A\u8D39\u66FC\u5B66\u4E60\u6CD5\uFF0C\u4F46\u5B83\u4E0D\u4EC5\u9650\u4E8E\u6570\u5B66\u6216\u7269\u7406\u5B66\uFF0C\u8FD9\u4E2A\u6280\u5DE7 <strong>\u51E0\u4E4E</strong> \u80FD\u7528\u5728\u4EFB\u4F55\u5B66\u79D1\u6216\u6982\u5FF5\u7684\u5B66\u4E60\u4E0A\u3002</p><p>\u8D39\u66FC\u5B66\u4E60\u6CD5\u7684\u7B2C\u4E00\u6B65\u662F:</p><h4 id="\u9009\u62E9\u4E00\u4E2A\u4E3B\u9898\u5E76\u5F00\u59CB\u7814\u7A76" tabindex="-1"><em>\u9009\u62E9\u4E00\u4E2A\u4E3B\u9898\u5E76\u5F00\u59CB\u7814\u7A76</em> <a class="header-anchor" href="#\u9009\u62E9\u4E00\u4E2A\u4E3B\u9898\u5E76\u5F00\u59CB\u7814\u7A76" aria-hidden="true">#</a></h4><p>\u9996\u5148\u9700\u8981\u660E\u786E\u4E00\u70B9\uFF0C\u5B66\u4E60\u4E0D\u4EC5\u4EC5\u610F\u5473\u7740\u8BB0\u4F4F\u4E8B\u5B9E\uFF0C\u5373<code>\u5B66\u4E60\u4E0D\u662F\u6B7B\u8BB0\u786C\u80CC</code>\u3002</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u8D39\u66FC\u672C\u4EBA\u4E00\u76F4\u53CD\u5BF9\u6B7B\u8BB0\u786C\u80CC\u6587\u5316\uFF0C\u4ED6\u59CB\u7EC8\u8BA4\u4E3A\u5E94\u8BE5 <strong>\u5B66\u4E60</strong> \u548C <strong>\u7406\u89E3</strong> \u539F\u7406\uFF0C\u800C\u4E0D\u662F\u6B7B\u8BB0\u786C\u80CC\u4E8B\u5B9E\u6216\u516C\u5F0F\u3002</p><blockquote><p>\u53E6\u4E00\u4E2A\u5B66\u4E1C\u897F\u7684\u597D\u65B9\u6CD5\u662F <strong>\u5199\u4F5C</strong>\u3002<br> \u5728\u7EB8\u4E0A\u5199\u4E1C\u897F\u4F1A\u523A\u6FC0\u5927\u8111\u7684\u6D77\u9A6C\u4F53\uFF0C\u800C\u8BE5\u90E8\u5206\u4E3B\u8981\u8D1F\u8D23\u7684\u5C31\u662F <strong>\u8BB0\u5FC6\u548C\u5B66\u4E60</strong>\u3002</p></blockquote><h3 id="\u7B2C\u4E8C\u6B65" tabindex="-1">\u7B2C\u4E8C\u6B65 <a class="header-anchor" href="#\u7B2C\u4E8C\u6B65" aria-hidden="true">#</a></h3><blockquote><p>\u5982\u679C\u4F60\u60F3\u8981\u6210\u4E3A\u67D0\u65B9\u9762\u7684\u5927\u5E08\uFF0C\u90A3\u4E48\u5C31\u5C06\u77E5\u8BC6\u4F20\u6388\u51FA\u53BB\u3002\u6559\u5B66\u672C\u8EAB\u5C31\u662F\u4E00\u4E2A\u5F3A\u5927\u7684\u5B66\u4E60\u5DE5\u5177\u3002</p></blockquote><h4 id="\u7528\u4F60\u81EA\u5DF1\u7684\u8BDD\u89E3\u91CA\u4E00\u4E9B\u6982\u5FF5" tabindex="-1"><em>\u7528\u4F60\u81EA\u5DF1\u7684\u8BDD\u89E3\u91CA\u4E00\u4E9B\u6982\u5FF5</em> <a class="header-anchor" href="#\u7528\u4F60\u81EA\u5DF1\u7684\u8BDD\u89E3\u91CA\u4E00\u4E9B\u6982\u5FF5" aria-hidden="true">#</a></h4><p>\u8BD5\u7740\u5C06\u5176\u4F5C\u4E3A\u4E3B\u9898\u5411\u513F\u7AE5\u6216\u8005\u90A3\u4E9B\u5F7B\u5F7B\u5E95\u5E95\u7684\u95E8\u5916\u6C49\u8BB2\u89E3\u8FD9\u4E9B\u77E5\u8BC6\u3002<br> \u5F53\u7136\uFF0C\u6559\u6388\u7684\u5BF9\u8C61\u89C6\u573A\u5408\u800C\u5B9A\uFF0C\u4F60\u751A\u81F3\u53EF\u4EE5\u5BF9\u4F60\u684C\u4E0A\u7684\u5C0F\u9EC4\u9E2D\u65BD\u5C55\u8FD9\u4E00\u6280\u5DE7\u3002</p><p>\u8FD9\u4E00\u6B65\u7684\u76EE\u7684\u662F\u5C3D\u53EF\u80FD\u7684\u5C06\u4E00\u4E2A\u590D\u6742\u7684\u6982\u5FF5\u62C6\u89E3\u7684\u7B80\u5355\u76F4\u767D\u3002\u4F60\u53EF\u80FD\u9700\u8981\u4E00\u4E24\u4E2A\u793A\u4F8B\u6765\u8BA9\u8FD9\u4EF6\u4E8B\u53D8\u5F97\u66F4\u7B80\u5355\uFF0C\u6216\u8005\u8BD5\u7740\u81EA\u5DF1\u521B\u9020\u4E00\u4E2A\u4F8B\u5B50\uFF0C\u53EA\u8981\u4FDD\u8BC1\u4ED6\u4EEC\u7684\u4E2D\u5FC3\u601D\u60F3\u662F\u76F8\u4E92\u5173\u8054\u7684\u5C31\u597D\u3002</p><p>\u8FD9\u6837\u4F60\u5C31\u80FD\u66F4\u52A0\u6DF1\u523B\u5730\u7406\u89E3\u4E8B\u7269\uFF0C\u5E76\u4E14\u4E0E\u4E4B\u5EFA\u7ACB\u8054\u7CFB\u3002</p><h3 id="\u7B2C\u4E09\u6B65" tabindex="-1">\u7B2C\u4E09\u6B65 <a class="header-anchor" href="#\u7B2C\u4E09\u6B65" aria-hidden="true">#</a></h3><p>\u8FD9\u662F\u81F3\u5173\u91CD\u8981\u7684\u4E00\u6B65\uFF0C\u53EF\u4EE5\u8BA9\u4F60\u53D1\u73B0\u4F60\u7684\u4E0D\u8DB3\u4E4B\u5904\u3002\u5F53\u4F60\u7528\u7B80\u5355\u7684\u672F\u8BED\u89E3\u91CA\u6216\u5199\u4E1C\u897F\u65F6\uFF0C\u4F60\u4F1A\u53D1\u73B0\u603B\u662F\u4F1A\u9047\u5230\u67D0\u4E9B\u9886\u57DF\uFF0C\u5F88\u96BE\u89E3\u91CA\u6216\u5EFA\u7ACB\u8054\u7CFB\u6216\u5236\u5B9A\u4F8B\u5B50\u3002</p><p>\u6B64\u65F6\u4F60\u5E94\u8BE5\u7ACB\u9A6C\u56DE\u5934\u7528\u5C3D\u65B9\u6CD5\u6765\u641C\u96C6\u8D44\u6599\u6765\u586B\u8865\u4F60\u7684\u77E5\u8BC6\u9E3F\u6C9F\u3002</p><blockquote><p>\u4F60\u53EF\u4EE5\u5C06\u8FD9\u4E9B\u6B20\u7F3A\u7684\u77E5\u8BC6\u5212\u51FA\u8303\u56F4\u8FDB\u884C\u5206\u7C7B\uFF0C\u4F8B\u5982\u65E0\u6CD5\u89E3\u91CA\u6216\u8005\u7B80\u5316\u7684\u90E8\u5206\uFF0C\u88AB\u9057\u5FD8\u7684\u91CD\u8981\u77E5\u8BC6\u70B9\uFF0C\u7B49\u7B49</p></blockquote><h4 id="\u9047\u5230\u95EE\u9898\u53CA\u65F6\u56DE\u5934\u67E5\u6F0F\u8865\u7F3A" tabindex="-1"><em>\u9047\u5230\u95EE\u9898\u53CA\u65F6\u56DE\u5934\u67E5\u6F0F\u8865\u7F3A</em> <a class="header-anchor" href="#\u9047\u5230\u95EE\u9898\u53CA\u65F6\u56DE\u5934\u67E5\u6F0F\u8865\u7F3A" aria-hidden="true">#</a></h4><p>\u8FD9\u5C06\u5E2E\u52A9\u4F60\u66F4\u597D\u7684\u7406\u89E3\u4E8B\u7269\u3002</p><p>\u5728\u8FD9\u4E00\u6B65\uFF0C\u4F60\u77E5\u9053\u4E86\u9700\u8981\u7528\u529F\u4EE5\u53CA\u5173\u6CE8\u7684\u9886\u57DF\uFF0C\u800C\u8FD9\u4E5F\u662F\u5B66\u4E60\u8FC7\u7A0B\u4E2D\u7684\u91CD\u8981\u90E8\u5206\u3002 \u4E86\u89E3\u81EA\u5DF1\u7684\u5C40\u9650\u6027\uFF0C\u7136\u540E\u52AA\u529B\u66F4\u597D\u5730\u7406\u89E3\u5B83\u4EEC\u662F\u8FD9\u4E00\u6B65\u7684\u91CD\u70B9\uFF0C\u6574\u4E2A\u8FC7\u7A0B\u5982\u540C\u9B54\u672F\u4E00\u822C\u3002</p><h3 id="\u7B2C\u56DB\u6B65" tabindex="-1">\u7B2C\u56DB\u6B65 <a class="header-anchor" href="#\u7B2C\u56DB\u6B65" aria-hidden="true">#</a></h3><br><h4 id="\u73B0\u5728\uFF0C\u6765\u8BD5\u7740\u4EA7\u51FA\u5427" tabindex="-1"><em>\u73B0\u5728\uFF0C\u6765\u8BD5\u7740\u4EA7\u51FA\u5427</em> <a class="header-anchor" href="#\u73B0\u5728\uFF0C\u6765\u8BD5\u7740\u4EA7\u51FA\u5427" aria-hidden="true">#</a></h4><p>\u4E00\u65E6\u4F60\u7EA0\u6B63\u4E86\u9519\u8BEF\uFF0C\u514B\u670D\u4E86\u56F0\u96BE\uFF0C\u4F60\u7684\u8BB2\u89E3\u5C31\u80FD\u5F97\u5230\u8FDB\u4E00\u6B65\u7684\u7B80\u5316\u3002\u4F60\u53EF\u4EE5\u968F\u65F6\u56DE\u5230\u7B2C\u4E8C\u6B65\u6216\u8005\u7B2C\u4E09\u6B65\uFF0C\u76F4\u5230\u4F60<strong>\u5B8C\u5168\u7406\u89E3\u4E86\u8FD9\u4E00\u5207</strong>\u3002</p><p>\u4F60\u7684\u7B14\u8BB0\u548C\u793A\u4F8B\u5DF2\u7ECF\u8D8B\u4E8E\u7B80\u5316\u7684\u9876\u5CF0\uFF0C\u540C\u65F6\u4F60\u5BF9\u4E8E\u5B66\u4E60\u7684\u4E3B\u9898\u6709\u4E86\u66F4\u6DF1\u7684\u7406\u89E3\uFF0C\u4F60\u5DF2\u7ECF\u5F00\u59CB\u638C\u63A7\u6574\u4E2A\u6982\u5FF5\u3002</p><p>\u7EC8\u4E8E\u4F60\u7684\u6700\u7EC8\u8BB2\u89E3\u7248\u672C\u5DF2\u7ECF\u51C6\u5907\u597D\u4E86\uFF0C\u4F60\u53EF\u4EE5\u5C06\u5176\u8868\u8FF0\u7ED9\u719F\u6089\u8FD9\u4E00\u9886\u57DF\u7684\u719F\u4EBA\uFF0C\u4F60\u7684\u670B\u53CB\uFF0C\u540C\u4E8B...\uFF0C\u5E76\u4E14\u901A\u8FC7\u4ED6\u4EEC\u7684\u53CD\u9988\u6765\u66F4\u65B0\u4F60\u5BF9\u8FD9\u4EF6\u4E8B\u7269\u7684\u7406\u89E3\u3002</p><p><code>\u8FD9\u79CD\u5728\u5B9E\u8DF5\u4E2D\u5B66\u4E60\u7684\u65B9\u5F0F\u53EF\u4EE5\u521B\u9020\u5947\u8FF9</code></p><blockquote><p>\u8D39\u66FC\u575A\u4FE1\uFF0C\u771F\u7406\u5728\u4E8E\u7B80\u5355\uFF0C\u7B80\u5355\u800C\u4F18\u96C5\u7684\u4E8B\u7269\uFF0C\u624D\u80FD\u66F4\u597D\u5730\u7406\u89E3\u3002<br> the truth lies in simplicity and that things can be better understood when they are simple and elegant.</p></blockquote>',33),h=[i];function d(o,s,l,c,p,u){return t(),a("div",null,h)}var g=e(n,[["render",d]]);export{_ as __pageData,g as default};
